<?php/** * Created by PhpStorm. * User: bruno * Date: 18/09/15 * Time: 17:46 */namespace App\Model\Behavior;use Cake\Cache\Cache;use Cake\ORM\Behavior;class CacheBehavior extends Behavior{    /**     * @param array $files     * @return array Vide le cache des catégories et pages     * Vide le cache des catégories et pages     */    private function delete_cache($files = []){        return Cache::deleteMany($files,'nav');    }    public function beforeSave($entity, $options = [])    {	    $this->delete_cache(['pages','navigation_footer']);    }    public function afterDelete()    {        $this->delete_cache(['pages','navigation_footer']);    }} 